{"process.name.short":{"type":"keyword","script":{"source":"// aul.processImagePath.keyword 필드에서 파일명만 추출하는 스크립트
String path = doc['aul.processImagePath.keyword'].value; 

if (path != null) {
    int lastSlashIndex = path.lastIndexOf('/');
    if (lastSlashIndex > 0) {
        emit(path.substring(lastSlashIndex + 1)); // 슬래시 뒤의 글자(파일명)만 내보냄
    } else {
        emit(path); // 슬래시가 없으면 그냥 내보냄
    }
}"}}}

{"tactic_ordered":{"type":"keyword","script":{"source":"String t = "";

// 1. 보안 알람에서 주로 쓰는 이름
if (doc.containsKey('kibana.alert.rule.threat.tactic.name') && !doc['kibana.alert.rule.threat.tactic.name'].empty) {
    t = doc['kibana.alert.rule.threat.tactic.name'].value;
}

// 번호표 붙이기
if (t == "Initial Access") { emit("01_Initial Access"); }
else if (t == "Execution") { emit("02_Execution"); }
else if (t == "Persistence") { emit("03_Persistence"); }
else if (t == "Privilege Escalation") { emit("04_PrivEsc"); }
else if (t == "Defense Evasion") { emit("05_Defense Evasion"); }
else if (t == "Credential Access") { emit("06_Credential Access"); }
else if (t == "Discovery") { emit("07_Discovery"); }
else if (t == "Lateral Movement") { emit("08_Lateral Movement"); }
else if (t == "Collection") { emit("09_Collection"); }
else if (t == "Command and Control") { emit("10_C2"); }
else if (t == "Exfiltration") { emit("11_Exfiltration"); }
else if (t == "Impact") { emit("12_Impact"); }
else if (t != "") { emit("99_" + t); } // 이름이 있으면 99번
else { emit("00_Unknown"); } "}}}
